const jwt = require('jsonwebtoken');
const { UnknownError } = require('./errors');

function makeGenerateToken({secretKey, expiresIn}) {
  return function generateToken(payload) {
    try {
      const token = jwt.sign(payload, secretKey, { expiresIn });
      return token;
    }
    catch (err) {
      throw new UnknownError(err.message);
    }
  }
}

function makeDecodeToken(secretKey) {
  return function decodeToken(token) {
    try {
      const decodedPayload = jwt.verify(token, secretKey);
      return decodedPayload;
    } catch {
      return null;
    }
  }
}

module.exports = {
  makeGenerateToken, makeDecodeToken
}